  # def evaluate_star_2(), do: evaluate_star_2(@str_2)

  def evaluate_star_2(str) do
    fun = fn
      ")" -> -1
      "(" -> 1
    end

    str
    |> String.split("", trim: true)
    |> Enum.map(fun)
    |> sum(0, 0)
  end

  def sum([h | t], acc, index)when acc != -1, do: sum(t, acc + h, index + 1)
  def sum(list, acc, index) when list == [] or acc == -1, do: index
end
